import React from "react";
import { Link } from "react-router-dom";

const Home = ({ blog}) => {
  const postBlog = blog.map(post => (
    <Link to= {`/post/${post.id}`}><div className="posts">
      <h2>{post.title}</h2>
      <h2>{post.category}</h2>
    </div></Link>
  ));
  return (
    <div className="landingPage">
      {" "}
      <Link to="/about"><h2 className='addPost'>Add post</h2></Link>
      {postBlog}
    </div>
  );
};

export default Home;
