import React from "react";
import { Link } from "react-router-dom";

const About = ({ user, onChange, onClick }) => {
  const { title, category, text } = user;
  return (
    <div className="newPost-wrapper">
      <div>
        <label>
          Title:
          <input key={title}type="text" name="title" value={title} onChange={onChange} />
        </label>
      </div>
      <div>
        <label>
          Category:
          <input
          key={category}
            type="text"
            name="category"
            value={category}
            onChange={onChange}
          />
        </label>
      </div>
      <div>
        <label>Write a new post:</label>
      </div>
      <div>
        <textarea

          type="text"
          name="text"
          value={text}
          onChange={onChange}
          className="textInput"
        />
      </div>
      <div>
        <Link to="/contact">
          <button>Save</button>
        </Link>

        <Link to="/contact">
          <button>Cancel</button>
        </Link>
      </div>
    </div>
  );
};

export default About;
