import React, { Component } from "react";
import Home from "./components/Home";
import About from "./components/About";
import Contact from "./components/Contact";
import { BrowserRouter, Route, Switch } from "react-router-dom";

class App extends Component {
  state = {
    user: {
      title: "",
      category: "",
      text: ""
    },

    defaultUser: [
      {
        id: 1,
        title: "My day in Integrify",
        category: "work",
        text:
          "simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."
      },
      {
        id: 2,
        title: "My day in Integrify",
        category: "work",
        text:
          "simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."
      }
    ]
  };
  handleChange = e => {
    const { name, value } = e.target;
    console.log(name, value);

    const user = {
      ...this.state.user,
      [name]: value,
      id: this.state.defaultUser.length + 1
    };

    this.setState({ user: user });
  };
  handleSubmit = e => {
    e.preventDefault();

    const copyUser = { ...this.state.user, id: 5 };
    const copyUsers = [...this.state.defaultUser, copyUser];

    this.setState({
      defaultUser: copyUsers
    });
  };
  deleteStudent = id => {
    const students = this.state.defaultUser.filter(
      student => id !== student.id
    );
    this.setState({ defaultUser: students });
  };

  render() {
    /*  console.log(this.state.user);
    console.log(this.state.defaultUser); */
    return (
      <BrowserRouter>
        <div className="App">
          <div>
            <Switch>
              <Route
                path="/home"
                exact
                component={() => <Home blog={this.state.defaultUser} />}
              />
              <Route
                path="/about"
                exact
                component={() => (
                  <About
                    user={this.state.user}
                    onChange={this.handleChange}
                    onClick={this.handleSubmit}
                  />
                )}
              />
              <Route
                path="/contact"
                exact
                component={() => (
                  <Contact
                    data={this.state.defaultUser}
                    del={this.deleteStudent}
                  />
                )}
              />
            </Switch>
          </div>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
