import React from "react";
import { Link } from "react-router-dom";
//import Home from './Home'
const About = ({ user, onChange, onSubmit, history }) => {
  console.log(history);
  const { title, category, text } = user;
  return (
    <div className="newPost-wrapper">
        <form>
      <div>
        <label>
          Title:
          <input
            key={title}
            type="text"
            name="title"
            value={title}
            onChange={onChange}
          />
        </label>
      </div>
      <div>
        <label>
          Category:
          <input
            key={category}
            type="text"
            name="category"
            value={category}
            onChange={onChange}
          />
        </label>
      </div>
      <div>
        <label>Write a new post:</label>
      </div>
      <div>
        <textarea
          key={text}
          type="text"
          name="text"
          value={text}
          onChange={onChange}
          className="textInput"
        />
      </div>
      <Link to="/">
        <button >Save</button>
      </Link>
      </form>
    </div>
  );
};

export default About;
